-- Clean up any existing pools and reinitialize all schools with 500 unique Pokemon
DELETE FROM public.pokemon_pools;

-- Now reinitialize all school pools with 500 unique Pokemon from the expanded catalog
DO $$
DECLARE
    school_record RECORD;
    catalog_pokemon RECORD;
    pokemon_array INTEGER[];
    selected_pokemon INTEGER[];
    pool_size INTEGER := 500;
    available_count INTEGER;
BEGIN
    -- Get count of available Pokemon in catalog
    SELECT COUNT(*) INTO available_count FROM public.pokemon_catalog;
    
    -- Adjust pool size if we don't have enough Pokemon
    IF available_count < pool_size THEN
        pool_size := available_count;
    END IF;
    
    -- Loop through each school
    FOR school_record IN SELECT id FROM public.schools LOOP
        -- Get all Pokemon IDs from catalog
        SELECT ARRAY(SELECT id FROM public.pokemon_catalog ORDER BY RANDOM()) INTO pokemon_array;
        
        -- Take first 500 (or available count) Pokemon
        selected_pokemon := pokemon_array[1:pool_size];
        
        -- Insert into pokemon_pools for this school
        SELECT school_record.id, unnest(selected_pokemon), 'available';
        
        RAISE NOTICE 'Initialized school % with % Pokemon', school_record.id, pool_size;
    END LOOP;
END $$;
